import UseAutocomplete from "./InputAuto"
import React from 'react';
import useAutocomplete from '@material-ui/lab/useAutocomplete';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(() => ({
  input: {"outline":"none","width":"95%","height":"2vw","borderRadius":"15px","border":"none","fontSize":"0.90vw","background":"rgba(255, 255, 255, 0.75)","backdropFilter":"blur(100px)","fontFamily":"'Quicksand'"}
  ,
  listbox: {
    width: "16%",
    borderRadius:"0 0 15px 15px",
    margin: 0,
    padding: 0,
    zIndex: 1,
    position: 'absolute',
    listStyle: 'none',
    backgroundColor: 'rgba(255, 255, 255, 0.75)',
    overflow: 'auto',
    maxHeight: 200,
    border: '1px solid rgba(0,0,0,.25)',
    '& li[data-focus="true"]': {
      backgroundColor: '#4a8df6',
      color: 'white',
      cursor: 'pointer',
    },
    '& li:active': {
      backgroundColor: '#2977f5',
      color: 'white',
    },
  },
}));

const InputCraft = () => {
  const classes = useStyles();

  const Show = ()=>{
      let Liste_items =[]
      for (let i=0;i<5;i++){
          i !== 4 ? Liste_items.push(<><UseAutocomplete id={i} cssStyle={classes.input}/><div className="operator-style">+</div></>) : Liste_items.push(<UseAutocomplete/>)
      }
      return Liste_items
  }


  return (
      <div className="menu-craft">
          <div className="container-input-craft" >
            {
              Show()
            }
          </div>
          <div className="operator-style" style={{"margin-left": "5%", "font-size":"2vw"}}>=</div>
          {/* <UseAutocomplete />/*className="input" id="input-items"*/ }
          <input className="input" id="input-items" />
      </div>
)}
export default InputCraft
